/* ---------- Trust Score class ---------- */
export function trustScoreClass(score) {
  if (score >= TRUST.HIGH) return 'score-high';
  if (score >= TRUST.MID) return 'score-mid';
  return 'score-low';
}

/* ---------- Star rating HTML ---------- */
export function starsHTML(rating, count) {
  if (!rating) return '<span class="text-muted text-sm">No reviews</span>';
  let html = '<div class="stars">';
  for (let i = 1; i <= 5; i++) {
    if (i <= Math.floor(rating)) {
      html += `<span class="star-filled">${ICONS.star}</span>`;
    } else if (i - 0.5 <= rating) {
      html += `<span class="star-filled">${ICONS.star}</span>`;
    } else {
      html += `<span class="star-empty">${ICONS.starEmpty}</span>`;
    }
  }
  html += `<span class="text-sm font-semibold" style="margin-left:4px">${rating}</span>`;
  if (count) html += `<span class="text-xs text-muted">(${count.toLocaleString()})</span>`;
  html += '</div>';
  return html;
}

/* ---------- Review platforms with logos ---------- */
export function reviewPlatformsHTML(reviews, size = 'sm') {
  if (!reviews) return '';
  const platforms = [];
  if (reviews.google) {
    const gClass = size === 'lg' ? 'g-logo g-logo-lg' : 'g-logo g-logo-sm';
    platforms.push(`<a href="#" class="review-platform review-platform-g" title="Google: ${reviews.google}/5">
      <span class="${gClass}">${ICONS.google}</span>
      <span class="review-platform-rating">${reviews.google}</span>
    </a>`);
  }
  if (reviews.trustpilot) {
    const tpClass = size === 'lg' ? 'tp-logo tp-logo-lg' : 'tp-logo tp-logo-sm';
    platforms.push(`<a href="#" class="review-platform review-platform-tp" title="Trustpilot: ${reviews.trustpilot}/5">
      <span class="${tpClass}">${ICONS.trustpilot}</span>
      <span class="review-platform-rating">${reviews.trustpilot}</span>
    </a>`);
  }
  if (reviews.feefo) {
    platforms.push(`<a href="#" class="review-platform" title="Feefo: ${reviews.feefo}/5">
      <span class="review-platform-icon feefo-icon">${ICONS.feefo}</span>
      <span class="review-platform-rating">${reviews.feefo}</span>
    </a>`);
  }
  if (!platforms.length) return '';
  return `<div class="review-platforms">${platforms.join('')}</div>`;
}

/* ---------- Regulatory badges ---------- */
export function regBadgesHTML(reg) {
  if (!reg) return '';
  const items = [];
  if (reg.arla) items.push({ icon: ICONS.arla, label: 'ARLA', key: 'arla' });
  if (reg.cmp) items.push({ icon: ICONS.cmp, label: 'CMP', key: 'cmp' });
  if (reg.tpo) items.push({ icon: ICONS.tpo, label: 'TPO', key: 'tpo' });
  if (reg.prs) items.push({ label: 'PRS', key: 'prs' });
  if (!items.length) return '';
  return `<div class="reg-badges">${items.map(i =>
    i.icon
      ? `<span class="reg-badge-logo" title="${i.label}">${i.icon}</span>`
      : `<span class="reg-badge verified">${ICONS.shieldCheck}${i.label}</span>`
  ).join('')}</div>`;
}

/* ---------- Social media icons for cards ---------- */
export function socialIconsHTML(social) {
  if (!social) return '';
  const links = [];
  if (social.facebook) links.push({ icon: ICONS.facebook, url: social.facebook, name: 'Facebook' });
  if (social.instagram) links.push({ icon: ICONS.instagram, url: social.instagram, name: 'Instagram' });
  if (social.youtube) links.push({ icon: ICONS.youtube, url: social.youtube, name: 'YouTube' });
  if (social.tiktok) links.push({ icon: ICONS.tiktok, url: social.tiktok, name: 'TikTok' });
  if (social.linkedin) links.push({ icon: ICONS.linkedin, url: social.linkedin, name: 'LinkedIn' });
  if (social.twitter) links.push({ icon: ICONS.twitter || ICONS.externalLink, url: social.twitter, name: 'X (Twitter)' });
  if (!links.length) return '';
  return `<div class="social-icons-row">${links.map(l =>
    `<a href="${l.url}" target="_blank" rel="noopener" title="${l.name}" class="social-icon-sm">${l.icon}</a>`
  ).join('')}</div>`;
}

/* ---------- Format helpers ---------- */
export function fmtFee(agent) {
  const f = agent.fees;
  if (f.mgmt_pct) return `${f.mgmt_pct}%`;
  if (f.full_management) return f.full_management;
  if (f.tenant_find) return f.tenant_find;
  return null;
}

export function fmtLocation(agent) {
  const parts = [];
  if (agent.address) parts.push(agent.address);
  if (agent.city && (!agent.address || !agent.address.toLowerCase().includes(agent.city.toLowerCase()))) parts.push(agent.city);
  if (agent.postcode) parts.push(agent.postcode);
  return parts.join(', ') || 'UK';
}

export function fmtAgentType(type) {
  const map = { high_street: 'High Street', online: 'Online', hybrid: 'Hybrid' };
  return map[type] || '';
}

